mapper.select:
SELECT count(*)
FROM product
WHERE finPrdtNm = #{finPrdtNm}
  AND mtrtInt = #{mtrtInt}
  AND etcNote = #{etcNote}
  AND type = #{type}

SELECT count(*)
FROM `option`
WHERE dclsMonth = #{dclsMonth}
  AND finCoNo = #{finCoNo}
  AND finPrdtCd = #{finPrdtCd}
  AND saveTrm = #{saveTrm}

SELECT count(*)
FROM product
WHERE type = #{type}

SELECT *
FROM product
WHERE type = #{type}
LIMIT #{offset}, #{limit}

SELECT dclsMonth, finCoNo, finPrdtCd, saveTrm, intrRate, intrRate2, type
FROM `option`
WHERE finPrdtCd = #{finPrdtCd}

SELECT count(*)
FROM `loan`
WHERE finPrdtCd = #{finPrdtCd}
AND dlyRate = #{dlyRate}
AND loanLmt = #{loanLmt}
